import React, { useState, useContext, useEffect } from 'react';
import { useCommon } from '../../../context/commonContext';
import { Calendar } from 'primereact/calendar';
import Form from "react-bootstrap/Form";
import { postWithAuth } from '../../../api/api';
import { apiUrls } from '../../../api/apiConfig';
import { AuthContext } from "../../../context/authcontext";
import ChipInput from '../../../components/chipInput';


const ModalOrganizacion = ({ onClose, title, isNeworganization, organization }) => {


    const commonContext = useCommon();
    const { getLoggedSession } = useContext(AuthContext);
    const userSession = getLoggedSession();
    const [isLoading, setIsLoading] = useState(false);
    const [organizationData, setOrganizationData] = useState(isNeworganization ? {
        users_limit: null,
        company_name: null,
        domains: [],
        contracts_limit: null,
    } : organization)

    const handleInputChange = (event) => {
        const { id, value, type, checked } = event.target;
        setOrganizationData((prevData) => ({
            ...prevData,
            [id]: type === "checkbox" ? !prevData[id] : value,
        }));
    };

    const handleFieldAdded = (data) => {
        setOrganizationData((prevData) => ({
            ...prevData,
            domain_list: data,
        }));
    };


    const handlePostForm = (e) => {
        const fetchData = async () => {
            setIsLoading(true);
            try {
                const data = await postWithAuth(
                    isNeworganization ?
                        apiUrls.organizations.create : apiUrls.organizations.edit(organization.id),
                    organizationData,
                    userSession
                );
                if (data.status === "success") {
                    commonContext.changeApiSuccess();
                    onClose();
                } else {

                }
            } catch (error) {
                commonContext.setMessage("Ha ocurrido un error conectando con el servidor");
                commonContext.setAlertType("danger");
                commonContext.openAlertHeader();
            } finally {
                setIsLoading(false);
            }
        };
        fetchData();
    };

    return (
        <div className="modal fade show custom-modal" id="kt_modal_create_project" tabindex="-1" aria-modal="true" role="dialog" style={{ display: 'block' }}>

            <div className="modal-dialog modal-dialog-centered modal-small mw-750px p-9">

                <div className="modal-content modal-rounded">

                    <div className="modal-header">

                        <h2>{title}</h2>


                        <div className="btn btn-sm btn-icon btn-active-color-primary" data-bs-dismiss="modal">
                            <a onClick={onClose}>
                                <i className="ki-outline ki-cross fs-1"></i>
                            </a>
                        </div>

                    </div>




                    <div className="modal-body scroll-y m-5">
                        <div className="container">
                            <div className="row">
                                <div className="col-12">  <label className="d-flex align-items-center fs-6 fw-semibold form-label mb-2">
                                    <span className="">Nombre de la organización</span>
                                    <span className="ms-1" data-bs-toggle="tooltip" aria-label="Renta original" data-bs-original-title="Introducir la renta total (€)" data-kt-initialized="1">

                                    </span>
                                </label>
                                    <input class="form-control form-control-solid text-negro" id='company_name' name='company_name' placeholder='Introduce el nombre de la organización' defaultValue={organizationData.company_name ? organizationData.company_name : ''} onChange={handleInputChange}></input></div>

                            </div>
                            <div className="row">
                                <div className="col-12 mt-2">  <label className="d-flex align-items-center fs-6 fw-semibold form-label mb-2">
                                    <span className="">Dominios aceptados</span>

                                </label>
                                    <ChipInput placeholder={"dominio.com + Enter"} chipsPreAdded={isNeworganization || organization.domains === undefined ? null : organization.domain_list} onFieldAdded={handleFieldAdded} />
                                </div>

                            </div>
                            <div className="row mt-3">
                                <div className="col-12">  <label class="form-label body_s_semibold text-azul-oscuro" for="">Número máximo de contratos</label>
                                    <input class="form-control form-control-solid  text-negro" type='number' inputMode='number' id='contracts_limit' name='contracts_limit' placeholder='20' defaultValue={organizationData.contracts_limit ? organizationData.contracts_limit : ''} onChange={handleInputChange} ></input></div>
                                <div className="col-12 mt-3">  <label class="form-label body_s_semibold text-azul-oscuro" for="">Número máximo de usuarios</label>
                                    <input class="form-control form-control-solid text-negro" type='number' inputMode='number' id='users_limit' name='users_limit' placeholder='5' defaultValue={organizationData.users_limit ? organizationData.users_limit : ''} onChange={handleInputChange} ></input></div>

                            </div>

                        </div>

                    </div>


                    <div className="modal-footer" style={{ border: 'none' }}>

                        <div className="column d-flex justify-content-between">

                            <button type="submit" id="kt_modal_new_target_submit" className="btn btn-dark-figma" onClick={handlePostForm}>
                                <span className="indicator-label text-white">
                                    {isLoading ? (
                                        'Cargando...'
                                    ) :
                                        (
                                            <>
                                                {isNeworganization ? "Confirmar nueva organización" : "Confirmar edición"}
                                            </>
                                        )
                                    }
                                </span>
                            </button>


                        </div>

                    </div>

                </div>

            </div >

        </div >
    );
}

export default ModalOrganizacion;
