import ReactApexChart from 'apexcharts';
import React from 'react';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
);

const BarGraph = () => {


    const data = {
        labels: ['SERRANO', 'GOYA', 'GRAN VIA', 'AVENIDA DA LIBERDADE', 'VIA DEI CONDOTTI'],
        datasets: [
            {
                label: 'Bonificaciones(€)',
                backgroundColor: ['#5BA6FF', '#FF9900', '#6AD49B', '#E94357', '#8757ED'],
                borderColor: 'rgba(75,192,192,1)',
                borderWidth: 0,
                borderRadius: 8,
                hoverBackgroundColor: [
                    'rgba(91, 166, 255, 0.75)',
                    'rgba(255, 153, 0, 0.75)',
                    'rgba(106, 212, 155, 0.75)',
                    'rgba(233, 67, 87, 0.75)',
                    'rgba(135, 87, 237, 0.75)'
                ],
                hoverBorderColor: [
                    'rgba(91, 166, 255, 1)',
                    'rgba(255, 153, 0,1)',
                    'rgba(106, 212, 155, 1)',
                    'rgba(233, 67, 87, 1)',
                    'rgba(135, 87, 237, 1)'
                ],
                data: [50000, 5000, 30000, 18000, 45000], // Your horizontal data

            },
        ],
    };

    const options = {

        scales: {
            x: {
                beginAtZero: true,
                grid: {
                    display: true, // Set to false to hide the background grid
                },
                ticks: {
                    color: 'rgba(0, 0, 0, 0.5)', // Color of x-axis labels
                },
            },
            y: {
                beginAtZero: true,
                grid: {
                    display: false, // Set to false to hide the background grid
                },
                ticks: {
                    color: 'rgba(0, 0, 0, 0.5)', // Color of y-axis labels
                },
            },
        },
        indexAxis: 'y', // Set to 'y' for horizontal bar chart
        plugins: {
            legend: {
                display: false, // Set to false to hide the legend
            },
            title: {
                display: false, // Set to false to hide the title
            },
        },
    };
    return (
        <div>
            <div id="chart">
                <Bar options={options} data={data} />
            </div>
        </div>
    );

}

export default BarGraph;
