import React, { useState, useContext, useEffect } from 'react';
import { useCommon } from '../../context/commonContext';
import ModalContrato from './components/modalUsuario';
import BarGraph from '../../components/barGraph';
import DatePickerWithCustomMenu from '../../components/dateRengePickerHlded';

import 'react-datepicker/dist/react-datepicker.css';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';


import ModalViewContrato from './components/modalViewContrato';
import DataTable from 'react-data-table-component';
import ConfirmationModal from '../../components/confirmationModal';
import { useNavigate } from 'react-router-dom';
import DropDownButtonSmall from '../../components/dropdownButtonSmall';
import ModalUsuario from './components/modalUsuario';
import DateRangePicker from '../../components/dateRengePickerHlded';

const UsuariosPage = () => {

    const commonContext = useCommon();
    const navigate = useNavigate();
    const [activeusuarios, setActiveusuarios] = useState(true);
    const [activeEvents, setActiveEvents] = useState(false);
    const [activeBono, setActiveBono] = useState(false);
    const [viewModal, setViewModal] = useState(false);

    const paginationOptions = {
        rowsPerPageText: 'Elementos por página:',
        rangeSeparatorText: 'de',
