import React, { useState } from 'react';
import { TextField, Box, Input } from '@mui/material';
import { LocalizationProvider, DatePicker } from '@mui/x-date-pickers';


const DateRangePicker = ({ startDateSelected, endDateSelected }) => {
    const [startDate, setStartDate] = useState(null);
    const [endDate, setEndDate] = useState(null);

    const handleSelectChange = (e) => {
        const selectedOption = e.target.value;
        const currentDate = new Date(); // Obtener la fecha actual
        let newStartDate = null;
        let newEndDate = null;

        // Calcular startDate y endDate según la opción seleccionada
        switch (selectedOption) {
            case 'Año actual':
                newStartDate = new Date(currentDate.getFullYear(), 0, 1); // 1 de enero del año actual
                newEndDate = new Date(currentDate.getFullYear() - 1, 11, 31); // 31 de diciembre del año anterior
                break;
            case 'Año anterior':
                newStartDate = new Date(currentDate.getFullYear() - 1, 0, 1); // 1 de enero del año anterior
                newEndDate = new Date(currentDate.getFullYear() - 1, 11, 31); // 31 de diciembre del año anterior
                break;
            case 'Últimos 12 meses':
                newStartDate = new Date(currentDate.getFullYear() - 1, currentDate.getMonth(), currentDate.getDate()); // Hace 12 meses desde hoy
                newEndDate = currentDate; // Fecha actual
                break;
            case 'Últimos 7 días':
                newStartDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), currentDate.getDate() - 7); // Hace 7 días desde hoy
                newEndDate = currentDate; // Fecha actual
                break;
            case 'Mes actual':
                newStartDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1); // Primer día del mes actual
                newEndDate = currentDate; // Fecha actual
                break;
            case 'Mes anterior':
                newStartDate = new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1); // Primer día del mes anterior
                newEndDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), 0); // Último día del mes anterior
                break;
            case '1 trimestre':
                newStartDate = new Date(currentDate.getFullYear(), 0, 1); // 1 de enero del año actual
                newEndDate = new Date(currentDate.getFullYear(), 2, 31); // 31 de marzo del año actual
                break;
            case '2 trimestre':
                newStartDate = new Date(currentDate.getFullYear(), 3, 1); // 1 de abril del año actual
                newEndDate = new Date(currentDate.getFullYear(), 5, 30); // 30 de junio del año actual
                break;
            case '3 trimestre':
                newStartDate = new Date(currentDate.getFullYear(), 6, 1); // 1 de julio del año actual
                newEndDate = new Date(currentDate.getFullYear(), 8, 30); // 30 de septiembre del año actual
                break;
            case '4 trimestre':
                newStartDate = new Date(currentDate.getFullYear(), 9, 1); // 1 de octubre del año actual
                newEndDate = new Date(currentDate.getFullYear(), 11, 31); // 31 de diciembre del año actual
                break;
            default:
                break;
        }

        // Actualizar el estado con las nuevas fechas
        setStartDate(newStartDate);
        setEndDate(newEndDate);
    };


    return (

        <Box display="flex" justifyContent="space-between" alignItems="center">
            <DatePicker
                label="Fecha inicio"
                className='form-control form-control-solid'
                value={startDate}
                onChange={(newValue) => {
                    setStartDate(newValue);
                    startDateSelected = newValue;
                }}
                renderInput={(params) => <TextField {...params} sx={{ fontFamily: 'Lexend', border: 'none' }} />}
            />
            <Box mx={2}>hasta</Box>
            <DatePicker
                label="Fecha fin"
                value={endDate}
                className='form-control form-control-solid'
                onChange={(newValue) => {
                    if (newValue && newValue > startDate) {
                        setEndDate(newValue);
                        endDateSelected = newValue;
                    } else {
                        alert('¡Seleccione una fecha posterior a la de inicio!');
                    }
                }}
                renderInput={(params) => <TextField {...params} sx={{ fontFamily: 'Lexend', border: 'none' }} />}
            />

            {/* <select className="form-select form-select-transparent hidden-select" style={{ width: '2em' }} onChange={(event) => handleSelectChange(event)}
                data-control="select2" data-hide-search="true" data-placeholder="Select an option" data-kt-table-widget-4="filter_status" data-select2-id="select2-data-4-iq3t" tabindex="-1" data-kt-initialized="1">
                <option value="Año actual">Año actual</option>
                <option value="Año anterior">Año anterior</option>
                <option value="Últimos 12 meses">Últimos 12 meses</option>
                <option value="Últimos 7 días">Últimos 7 días</option>
                <option value="Mes actual">Mes actual</option>
                <option value="Mes anterior">Mes anterior</option>
                <option value="1 trimestre">1 trimestre</option>
                <option value="2 trimestre">2 trimestre</option>
                <option value="3 trimestre">3 trimestre</option>
                <option value="4 trimestre">4 trimestre</option>
            </select> */}
        </Box>

    );
};

export default DateRangePicker;
