import React, { useState } from 'react';
import { makeStyles } from '@mui/styles';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import { ReactComponent as EnglishFlag } from './english-flag.svg'; // Replace './english-flag.svg' with the path to your English flag SVG
import { ReactComponent as SpanishFlag } from './spanish-flag.svg'; // Replace './spanish-flag.svg' with the path to your Spanish flag SVG

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
}));

const FlagSelector = () => {
    const classes = useStyles();
    const [selectedFlag, setSelectedFlag] = useState('');

    const handleFlagChange = (event) => {
        setSelectedFlag(event.target.value);
    };

    return (
        <FormControl className={classes.formControl}>
            <Select
                value={selectedFlag}
                onChange={handleFlagChange}
                displayEmpty
                inputProps={{ 'aria-label': 'Select flag' }}
            >
                <MenuItem value="">
                    <em>Select flag</em>
                </MenuItem>
                <MenuItem value="english">
                    <EnglishFlag style={{ width: '24px', marginRight: '8px' }} />
                    English
                </MenuItem>
                <MenuItem value="spanish">
                    <SpanishFlag style={{ width: '24px', marginRight: '8px' }} />
                    Spanish
                </MenuItem>
            </Select>
        </FormControl>
    );
};

export default FlagSelector;
