import React, { useState, useContext, useEffect } from 'react';
import { useCommon } from '../../context/commonContext';

import DataTable from 'react-data-table-component';
import ConfirmationModal from '../../components/confirmationModal';
import { useNavigate } from 'react-router-dom';
import ModalUsuario from './components/modalUsuario';
import { AuthContext } from "../../context/authcontext";
import { apiUrls } from '../../api/apiConfig';
import { getWithAuth } from '../../api/api';

import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';
import CircularProgress from '@mui/material/CircularProgress';



const UsuariosPage = () => {

    const commonContext = useCommon();
    const [open, setOpen] = useState(false);
    const { getLoggedSession } = useContext(AuthContext);
    const userSession = getLoggedSession();
    const [viewModal, setViewModal] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState('');
    const [numberOfItems, setnumberOfItems] = useState(0);


    const paginationOptions = {
        rowsPerPageText: 'Elementos por página:',
        rangeSeparatorText: 'de',
        noRowsPerPage: false,
        selectAllRowsItem: true,
        selectAllRowsItemText: 'Todos',
    };
    const CustomLoader = () => <div>Cargando datos...</div>;
    const NoDataComponent = () => <div>No hay datos para mostrar, lo sentimos mucho</div>;



    const [usuariosData, setusuariosData] = useState(
        [

        ]);
    const [filteredUsuariosData, setFilteredUsuariosData] = useState(usuariosData);

    const deleteUser = (emailToDelete) => {
        const updatedUsers = usuariosData.filter(user => user.email !== emailToDelete);
        setusuariosData(updatedUsers);
        commonContext.setConfirmationModal(false);
    };

    const [organizacionesData, setOrganizacionesData] = useState([

    ]);

    const handleSearchInputChange = (event) => {
        const text = event.target.value;
        setSearchQuery(text);
        const filtered = text === "" ? usuariosData : usuariosData.filter(
            (customer) =>
                customer.nombre.toLowerCase().includes(text.toLowerCase()) ||
                customer.email.toLowerCase().includes(text.toLowerCase()) ||
                customer.empresa.includes(text)
        );
        setFilteredUsuariosData(filtered);
    };

    function getMatchingOrganization(row) {
        return organizacionesData.find(org => org.id === row.org_id);
    }


    useEffect(() => {
        const fetchOrgData = async () => {
            setIsLoading(true);
            try {
                const data = await getWithAuth(
                    apiUrls.organizations.list,
                    userSession
                );
                setOrganizacionesData(data.results);
            } catch (error) {
                /*  ctx.setMessage("Ha ocurrido un error conectando con el servidor");
                 ctx.setAlertType("danger");
                 ctx.openAlertHeader(); */
                console.error("Error getting cards:", error);
            } finally {
                setIsLoading(false);
            }
        };
        fetchOrgData();
        const fetchData = async () => {
            setIsLoading(true);
            try {
                const data = await getWithAuth(
                    apiUrls.users.list,
                    userSession
                );
                setnumberOfItems(data.results.length);
                setusuariosData(data.results);
            } catch (error) {
                /*  ctx.setMessage("Ha ocurrido un error conectando con el servidor");
                 ctx.setAlertType("danger");
                 ctx.openAlertHeader(); */
                console.error("Error getting cards:", error);
            } finally {
                setIsLoading(false);
            }
        };
        fetchData();
    }, [commonContext.apiSuccess]);




    return (
        <div className='w-100'>
            <div id="kt_app_toolbar d-flex" className="app-toolbar">

                <div id="kt_app_toolbar_container" className="app-container container-fluid d-flex flex-wrap justify-content-between">

                    <div className="page-title d-flex flex-column justify-content-center me-3 mb-6 mb-lg-0">

                        <h1 className="page-heading d-flex text-gray-900 fw-bold fs-3 flex-column justify-content-center me-3 my-0 title__heading_bold" >Usuarios con acceso</h1>


                        <ul className="breadcrumb breadcrumb-separatorless fw-semibold fs-7 my-0 pt-1">

                            <span className='title__muted_grey'>Gestiona tus usuarios y accesos a la plataforma</span>

                        </ul>

                    </div>


                    <div className="d-flex align-items-center gap-2 gap-lg-3">

                        <div className="d-flex align-center">
                            <a href="#!" onClick={() => commonContext.openshowNewModal()} className="btn btn-dark-figma d-flex flex-center flex-shrink-0 ms-4 h-40px text-white" data-bs-toggle="modal" data-bs-target="#kt_modal_create_project">
                                <svg width="16" height="15" viewBox="0 0 16 15" fill="none" xmlns="http://www.w3.org/2000/svg">
                                    <path d="M10.0002 7.5H8.00016M8.00016 7.5H6.00016M8.00016 7.5V5.5M8.00016 7.5V9.5M4.66683 1.72534C5.67984 1.13927 6.82983 0.831533 8.00016 0.833336C11.6822 0.833336 14.6668 3.818 14.6668 7.5C14.6668 11.182 11.6822 14.1667 8.00016 14.1667C4.31816 14.1667 1.3335 11.182 1.3335 7.5C1.3335 6.286 1.65816 5.14667 2.2255 4.16667" stroke="white" stroke-linecap="round" />
                                </svg>
                                Crear nuevo acceso</a>
                        </div>

                    </div>

                </div>

            </div>

            <div className="mt-15">

                <div id="kt_app_content" className="app-content flex-column-fluid">

