import React, { useState, useContext, useEffect } from 'react';
import { useCommon } from '../../context/commonContext';

import DataTable from 'react-data-table-component';
import ConfirmationModal from '../../components/confirmationModal';
import { useNavigate } from 'react-router-dom';
import ModalUsuario from './components/modalUsuario';
import { AuthContext } from "../../context/authcontext";
import { apiUrls } from '../../api/apiConfig';
import { getWithAuth } from '../../api/api';

import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';
import CircularProgress from '@mui/material/CircularProgress';



const UsuariosPage = () => {






    return (
        <div className='w-100'>
            <div id="kt_app_toolbar d-flex" className="app-toolbar">

                <div id="kt_app_toolbar_container" className="app-container container-fluid d-flex flex-wrap justify-content-between">

                    <div className="page-title d-flex flex-column justify-content-center me-3 mb-6 mb-lg-0">

                        <h1 className="page-heading d-flex text-gray-900 fw-bold fs-3 flex-column justify-content-center me-3 my-0 title__heading_bold" >Usuarios con acceso</h1>


                        <ul className="breadcrumb breadcrumb-separatorless fw-semibold fs-7 my-0 pt-1">

                            <span className='title__muted_grey'>Gestiona tus usuarios y accesos a la plataforma</span>

                        </ul>

                    </div>


                    <div className="d-flex align-items-center gap-2 gap-lg-3">

                        <div className="d-flex align-center">
                            <a href="#!" onClick={() => commonContext.openshowNewModal()} className="btn btn-dark-figma d-flex flex-center flex-shrink-0 ms-4 h-40px text-white" data-bs-toggle="modal" data-bs-target="#kt_modal_create_project">
                                <svg width="16" height="15" viewBox="0 0 16 15" fill="none" xmlns="http://www.w3.org/2000/svg">
                                    <path d="M10.0002 7.5H8.00016M8.00016 7.5H6.00016M8.00016 7.5V5.5M8.00016 7.5V9.5M4.66683 1.72534C5.67984 1.13927 6.82983 0.831533 8.00016 0.833336C11.6822 0.833336 14.6668 3.818 14.6668 7.5C14.6668 11.182 11.6822 14.1667 8.00016 14.1667C4.31816 14.1667 1.3335 11.182 1.3335 7.5C1.3335 6.286 1.65816 5.14667 2.2255 4.16667" stroke="white" stroke-linecap="round" />
                                </svg>
                                Crear nuevo acceso</a>
                        </div>

                    </div>

                </div>

            </div>

            <div className="mt-15">

                <div id="kt_app_content" className="app-content flex-column-fluid">

                    <div id="kt_app_content_container" className="app-container container-fluid">
                        <div className="col-xl-12 mb-5 mb-xl-12">


                            <div className="card card-flush h-xl-100">
                                <div className="card-header pt-7">
                                    <h3 className="card-title align-items-start flex-column">
                                        <span className="card-label fw-bold fs-7 lh-1 fw-bold py-0 w-auto text_rojo_figma">
                                            Todos los usuarios
                                        </span>
                                        <span className="text-gray-500 mt-3 fw-semibold fs-6 ">Total {numberOfItems} usuarios activos</span>
                                    </h3>


                                    <div className="card-toolbar">

                                        <div className="d-flex flex-stack flex-wrap gap-4">
                                            {/* 
                                            <div data-kt-daterangepicker="true" data-kt-daterangepicker-opens="left" className=" d-flex align-items-center px-4" data-kt-initialized="1">

                                                <div className=" fw-bold">
                                                    <DateRangePicker />
                                                </div>
                                            </div>
                                            */}

                                            <div className="d-flex align-items-center fw-bold ">
                                                <div className="text-gray-500 fs-7 me-2">Seleccionar organización</div>

                                                <Autocomplete
                                                    id="asynchronous-demo"
                                                    sx={{
                                                        width: 300,
                                                        '& .MuiAutocomplete-inputRoot': {
                                                            height: '2em',
                                                            borderColor: 'red',
                                                            ":focus": {
                                                                borderColor: 'red',
                                                            },
                                                            fontFamily: 'Sen'
                                                        },
                                                        '& .MuiAutocomplete-option': {
                                                            backgroundColor: 'red',
                                                            fontFamily: 'Sen !important'
                                                        },
                                                    }}
                                                    open={open}
                                                    onOpen={() => {
                                                        setOpen(true);
                                                    }}
                                                    onClose={() => {
                                                        setOpen(false);
                                                    }}
                                                    getOptionLabel={(option) => option.company_name}
                                                    onChange={(e, value) => {
                                                        //TODO: filtrar por organización
                                                    }}
                                                    options={organizacionesData}
                                                    loading={isLoading}
                                                    renderInput={(params) => (
                                                        <TextField
                                                            {...params}
                                                            InputProps={{
                                                                ...params.InputProps,
                                                                endAdornment: (
                                                                    <React.Fragment>
                                                                        {isLoading ? <CircularProgress color="inherit" size={20} /> : null}
                                                                        {params.InputProps.endAdornment}
                                                                    </React.Fragment>
                                                                ),
                                                            }}
                                                        />
                                                    )}
                                                />

                                            </div>


                                            <div className="position-relative my-1">
                                                <i className="ki-outline ki-magnifier fs-2 position-absolute top-50 translate-middle-y ms-4"></i>
                                                <input type="text" data-kt-table-widget-4="search" className="form-control w-300px fs-7 ps-12" value={searchQuery} onChange={handleSearchInputChange} placeholder="Buscar por nombre, email…" />
                                            </div>

                                        </div>

                                    </div>

                                </div>


                                <div className="mt-10">
                                    <div id="kt_table_widget_4_table_wrapper" className="dataTables_wrapper dt-bootstrap4 no-footer">
                                        <div className="table-responsive">
                                            <DataTable
                                                columns={[
                                                    {
                                                        name: "NOMBRE",
                                                        selector: row => row.name,
                                                        sortable: true,

                                                    },
                                                    {
                                                        name: "CORREO ELECTRÓNICO",
                                                        selector: row => row.email,

                                                    },
                                                    {
                                                        name: "ORGANIZACIÓN", selector: row => getMatchingOrganizationName(row), sortable: true
                                                    },
                                                    {
                                                        //BOTON DE ACCION SIN TITULO EN LA TABLA
                                                        name: "",
                                                        cell: (row) => (
                                                            <>
                                                                <div className="row">

                                                                    <a href="#" onClick={() => {
                                                                        commonContext.setConfirmationModal(true);
                                                                        commonContext.setSelectedObject(row);
                                                                    }} class="btn btn-secondary"><i class="fas fa-trash fs-4 me-2"></i> Eliminar</a>
                                                                </div>
                                                                {'     '}
                                                            </>
                                                        ),
                                                        ignoreRowClick: true,
                                                        maxWidth: "150px",
                                                        minWidth: "50px",
                                                    }
                                                ]}
                                                data={searchQuery !== '' ? filteredUsuariosData : usuariosData}
                                                highlightOnHover={true}
                                                responsive={true}
                                                customStyles={{
                                                    rows: {
                                                        style: {
                                                            minHeight: '72px',
                                                            color: 'var(--Gris, #8C8E8F)',
                                                            fontFamily: 'Sen',
                                                            fontSize: '14px',
                                                            fontStyle: 'normal',
                                                            fontWeight: 500,
                                                            lineHeight: '25px',
                                                        },
                                                    },
                                                    headCells: {
                                                        style: {
                                                            paddingLeft: '8px',
                                                            paddingRight: '8px',
                                                        },
                                                    },
                                                    cells: {
                                                        style: {
                                                            paddingLeft: '8px',
                                                            paddingRight: '8px',
                                                        },
                                                    },
                                                }}
                                                pagination
                                                className='table col-sm-12 align-middle table-roe fs-6'
                                                paginationComponentOptions={paginationOptions}
                                                progressComponent={<CustomLoader />}
                                                noDataComponent={<NoDataComponent />}
                                            />
                                        </div>
                                    </div>



                                </div>

                                <div className="card-footer pt-2">

                                </div>
                            </div>
                        </div>


                    </div>

                </div>

            </div>

            {commonContext.confirmationModal && (
                <ConfirmationModal
                    title={'Eliminar'}
                    header={'¿Desdea eliminar el usuario?'}
                    subtitle={'Esta acción es irreversible'}
                    acceptButtonText={'Aceptar'}
                    declineButtonText={'Cancelar'}
                    onClose={() => commonContext.setConfirmationModal(false)}
                    onAccept={() => deleteUser(commonContext.selectedObject.email)} />)}

            {commonContext.showNewModal && (
                <ModalUsuario isNewUser={true} title={'Nuevo usuario'} onClose={() => commonContext.closeshowNewModal()} />
            )}



        </div >

