import React, { createContext, useContext, useState, useEffect } from "react";
import { useNavigate, useLocation, Navigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

// Create the authentication context
export const AuthContext = createContext();

const saveSession = (userSession) => {
    localStorage.setItem("userSession", JSON.stringify(userSession));
};

const retrieveSession = () => {
    const session = localStorage.getItem("userSession");
    return JSON.parse(session) ?? null;
};

// Authentication context provider
export const AuthProvider = ({ children }) => {
    const navigate = useNavigate();
    const location = useLocation();
    const [authData, setAuthData] = useState({
        isAuthenticated: false,
        idToken: null,
        accessToken: null,
        sessionToken: null,
        type_user: null,
    });

    useEffect(() => {
        const storedSession = retrieveSession();
        if (storedSession) {
            setAuthData(storedSession);
        }
    }, []);

    const login = (idToken, accessToken, sessionToken) => {
        const userSession = {
            isAuthenticated: true,
            idToken,
            accessToken,
            sessionToken,
            type_user: jwtDecode(sessionToken).user.role,
        };
        setAuthData(userSession);
        saveSession(userSession);
    };

    const getLoggedSession = () => {
        return authData.isAuthenticated ? authData : null;
    };

    const logout = () => {
        setAuthData({
            isAuthenticated: false,
            idToken: null,
            accessToken: null,
            sessionToken: null,
            type_user: null,
        });
        localStorage.removeItem("userSession");
        navigate("/login");
    };

    return (
        <AuthContext.Provider value={{ getLoggedSession, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};

export const ProtectedRoute = ({ children }) => {
    const auth = useAuth();
    const location = useLocation();

    if (!auth.getLoggedSession()) {
        return <Navigate to={`/login?redirect=${location.pathname}`} />;
    }

    return children;
};
